<?phpinclude "functions.php";//Check if user is legitimately logged insession_start();if (!isset($_SESSION["valid_user"])){	// User not logged in, redirect to login page	header("Location: index.html");	exit();}echo "<table align='center' border='0' bgcolor='white'>		<tr><td colspan='4'><hr></td></tr>
		<tr>		<th>Date/Time</th>
		<th>Name</th>
		<th>Location</th>		<th align='right'>Card</th>
		</tr>		<tr><td colspan='4'><hr></td></tr>"; iConnect();$data = mysql_query("select log.faccode, log.cardno, log.logdt, log.accresult, loc.location from pdm_logs log, pdm_locations loc where loc.id = log.location order by log.logdt desc limit 13") or die(mysql_error());while($info = mysql_fetch_array($data)) { 	$faccode=$info['faccode'];	$cardno=$info['cardno'];	$logdt=$info['logdt'];	$logdt=date('d/m/Y H:i',strtotime($logdt));	$accresult=$info['accresult'];	$location=$info['location'];	if ($accresult=='A') // or another access denied event for a known card.. eg expired	{		$data2 = mysql_query("select firstname,surname from pdm_cards where faccode=$faccode and cardno=$cardno") or die(mysql_error());		$info2 = mysql_fetch_array($data2);		$firstname=$info2['firstname'];		$surname=$info2['surname'];		$textcolour="green";	}	else // unknown card	{		$firstname="Unknown";		$surname="card";		$textcolour="red";	}	echo "<tr align='left'>";	echo "<td auto='16%'>$logdt</td>";	echo "<td auto='16%'><font color='$textcolour'> $firstname $surname</font></td>";	//echo "<td width='16%'></td>";	//echo "<td width='16%'>$accresult</td>";	echo "<td width='16%'>$location</td>";	echo "<td auto='16%' align='right' ><a href='editcard.php'><font color='$textcolour'>[$faccode] $cardno</font></a></td>";	echo "</tr>";}
echo "</table>"; ?>