<?phpinclude "functions.php";//Check if user is legitimately logged insession_start();if (!isset($_SESSION["valid_user"])){	// User not logged in, redirect to login page	header("Location: index.html");	exit();}$imagepath="./cardholderimages/Unknown.jpg";$firstname="Unknown";$surname="ID Card";$mysqli = iConnect2();$data = $mysqli->query("select log.cardno, log.faccode, log.logdt, log.accresult, loc.location  from pdm_logs log, pdm_locations loc  where loc.id = log.location  order by log.logdt desc limit 1;");$num_rows = $data->num_rows;if ($num_rows > 0){	$row = $data->fetch_array();	$faccode=$row['faccode'];	$cardno=$row['cardno'];	$logdt=$row['logdt'];	$logdt=date('d/m/Y H:i',strtotime($logdt));	$accresult=$row['accresult'];	$location=$row['location'];		$data = $mysqli->query("select firstname, surname, imagepath from pdm_cards where cardno='$cardno' and faccode='$faccode'");	$num_rows = $data->num_rows;	if ($num_rows > 0)	{		$row = $data->fetch_assoc();		$firstname=$row['firstname'];		$surname=$row['surname'];		$imagepath=$row['imagepath'];	}		if ($accresult=='A') // or another access denied event for a known card.. eg expired		$resulttext="<font color='green'>Access Allowed</font>";	else	  $resulttext="<font color='red'>Access Denied</font>";}echo "<table align='center' border='0' bgcolor='white'><tr><td><h2>Last Access Event</h2></td></tr>";echo "<tr><td><img src='$imagepath'></td></tr>";echo "<tr><td>$firstname $surname</td></tr>";echo "<tr><td>$logdt</td></tr>";echo "<tr><td>$resulttext at $location</td></tr>";echo "</table>";?>