<?phpinclude "functions.php";//Check if user is legitimately logged insession_start();if (!isset($_SESSION["valid_user"])){	// User not logged in, redirect to login page	header("Location: index.html");	exit();}echo "<table align='center' border='0' bgcolor='white'>        <tr><td colspan='3' align='center' ><h2>Locations</h2></td></tr>		<tr><td colspan='3'><hr></td></tr>		<tr>		<th>ID</th>		<th>Name</th>		<th></th>		</tr>		<tr><td colspan='3'><hr></td></tr>"; $mysqli = iConnect2();$data = $mysqli->query("select id, location  from pdm_locations  order by id");$num_rows = $data->num_rows;if ($num_rows > 0){	while ($row = $data->fetch_array())	{		//$row = $data->fetch_array();		$id=$row['id'];		$location=$row['location'];				echo "<tr align='left'>";		echo "<td width='16%'>$id</td>";		echo "<td width='16%'>$location</td>";		echo "<td width='16%' align='right' ><form><input type='button' value='Edit'";		?>onClick="javascript:popup('editlocation.php?id=<?php echo "$id" ?> ','Edit',400,300)"></form></td><?php		echo "</tr>";	}		echo "</table>"; }?>